import React from "react";
import "./App.css";
import compass from "./images/compass.png";
import PageContent from "./PageContent.js";

export default class Compass extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeSection: props.section,
    };
    // this.scrollDown = this.scrollDown.bind(this);
    // this.scrollUp = this.scrollUp.bind(this);
  }

  componentDidUpdate(prevProps) {
    // Typical usage (don't forget to compare props):
    if (this.props.section !== prevProps.section) {
      this.setState((this.activeSection = this.props.section));
    }
  }

  render() {
    return (
      <div className="Compass-menu">
        <div className="Compass-horizontal-item left">
          <span className="Compass-link" onClick={this.props.scroll()}>evaluating</span>
        </div>
        <div className="Compass-vertical-container">
          <div className="">
            <span className="Compass-link">analysing</span>
          </div>
          <img src={compass} alt="Compass"></img>
          <div className="">
            <span className="Compass-link">ideating</span>
          </div>
        </div>
        <div className="Compass-horizontal-item right">
          <span className="Compass-link">building</span>
        </div>
      </div>
    );
  }
}
