import React from "react";
import "./App.css";
import compass from "./images/compass.png";
import TOC from "./TOC";

export default class Compass extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      menuOpen: false,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidUpdate(prevProps) {
    // Typical usage (don't forget to compare props):
    if (this.props.section !== prevProps.section) {
      this.setState((this.activeSection = this.props.section));
    }
  }

  //   <TOC
  //   headings={[
  //     { value: "analysing", id: "analyse" },
  //     { value: "ideating", id: "ideate" },
  //     { value: "building", id: "build" },
  //     { value: "evaluating", id: "evaluate" },
  //   ]}
  // />

  handleChange = () => {
    this.setState(() => {
      return { menuOpen: !this.state.menuOpen };
    });
  };

  render() {
    return (
      <div className="Compass-menu">
        <div className="Compass-horizontal-container left">
          <a className={"Compass-link" + menuOpen ? "hidden" : null} href="#evaluate">
            evaluating
          </a>
        </div>
        <div className="Compass-vertical-container">
          <a className="Compass-link" href="#analyse">
            analysing
          </a>
          <img
            src={compass}
            alt="Compass"
            onClick={() => this.handleChange()}
          ></img>
          <a className="Compass-link" href="#ideate">
            ideating
          </a>
          {this.state.menuOpen ? (
            <ul className="Nav-menu">
              <li>
                <a className="Compass-link" href="#analyse" onClick={() => this.handleChange()}>
                  analysing
                </a>
              </li>
              <li>
                <a className="Compass-link" href="#ideate" onClick={() => this.handleChange()}>
                  ideating
                </a>
              </li>
              <li>
                <a className="Compass-link" href="#build" onClick={() => this.handleChange()}>
                  building
                </a>
              </li>
              <li>
                <a className="Compass-link" href="#evaluate" onClick={() => this.handleChange()}>
                  evaluating
                </a>
              </li>
            </ul>
          ) : null}
        </div>
        <div className="Compass-horizontal-container right">
          <a className="Compass-link" href="#build">
            building
          </a>
        </div>
      </div>
    );
  }
}
