import React from "react";
import "./App.css";

class Parallax extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isDark: this.props.theme,
      title: this.props.title,
      message: this.props.message,
    };
  }

  render() {
    return (
      <div>
        <div className="parallax">
          <p
            className={this.props.isDark ? "paragraph-dark" : "paragraph-light"}
          >
            {this.props.title}
          </p>
        </div>
        <div
          className={
            this.props.isDark ? "parallax-box-dark" : "parallax-box-light"
          }
        >
          <p
            className={this.props.isDark ? "paragraph-dark" : "paragraph-light"}
          >
            {this.props.message}
          </p>
        </div>
      </div>
    );
  }
}

export default Parallax;
