import React from "react";
import "./App.css";
import compass from "./images/compass.png";

export default class Compass extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeSection: props.section,
    };
    this.scrollDown = this.scrollDown.bind(this);
    this.scrollUp = this.scrollUp.bind(this);
  }

  scrollDown = () => {};

  scrollUp = () => {};

  componentDidUpdate(prevProps) {
    // Typical usage (don't forget to compare props):
    if (this.props.section !== prevProps.section) {
      this.setState((this.activeSection = this.props.section));
    }
  }

  render() {
    return (
      <div className="Compass-menu">
        <div className="Compass-menu-item side"><span>four</span></div>
        <div className="Compass-container vertical">
          <div className="Compass-menu-item">one</div>
          <img src={compass} alt="Compass"></img>
          <div className="Compass-menu-item">three</div>
        </div>
        <div className="Compass-menu-item">two</div>
      </div>
    );
  }
}
